import { Container } from "react-bootstrap";
import NavBar from "./Component/NavBar";
import MovieList from "./Component/MovieList";
import axios from "axios";
import { useEffect, useState } from "react";


function App() {
  const [movies , setMovies] = useState([]);
  const AllMovies = async() => {
    const responsive =await axios.get('https://api.themoviedb.org/3/movie/popular?api_key=0d4cbfe47f14bc16cfefd10fa3dfc816&language=ar');
    setMovies(responsive.data.results)
  };
    const search = async(i) => {
      const res = await axios.get(`https://api.themoviedb.org/3/search/movie?api_key=0d4cbfe47f14bc16cfefd10fa3dfc816&query=${i}&language=ar`);
      setMovies(res.data.results)
    }
  useEffect(()=> {
    AllMovies();
  },[])
  return (
    <div className="App">
      <NavBar />
      <Container>
        <MovieList movies = {movies}/>
      </Container>
    </div>
  );
}

export default App;
