import axios from "axios";
import { Container, Form, Navbar } from "react-bootstrap";
import { useDispatch } from "react-redux";
import { Link } from "react-router-dom";
import { getAllMovies } from "../Redux/Actions/moviesAction";

function NavBar() {
const onSearch = (word)=> {
    search(word)
}
const dispatch =useDispatch();
const search = async(word) => {
    
    if(word === '') {
        dispatch(getAllMovies())
    }else{
    const res = await axios.get(`https://api.themoviedb.org/3/search/movie?api_key=0d4cbfe47f14bc16cfefd10fa3dfc816&query=${word}&language=ar`);;
    }
}
    return (
        
        <Navbar expand="lg" className="bg-body-tertiary">
        <Container >
            <Link className = 'navbar-brand' to="/">EgyBest</Link>
            <Navbar.Toggle aria-controls="navbarScroll" />
            <Navbar.Collapse id="navbarScroll" style={{justifyContent: "flex-end"}}>
                <Form className="d-flex" style={{width: '95%'}}>
                <Form.Control onChange={(e) => {onSearch(e.target.value)}}
                    type="search"
                    placeholder="Search"
                    className="me-2"
                    aria-label="Search"
                />
                </Form>
            </Navbar.Collapse>
            </Container>
        </Navbar>
    )
}

export default NavBar ;
