import { Container } from "react-bootstrap";
import NavBar from "./Component/NavBar";
import MovieList from "./Component/MovieList";
import axios from "axios";
import { useEffect, useState } from "react";


function App() {
  const [movies , setMovies] = useState([]);
  const AllMovies = async() => {
    const responsive =await axios.get('https://api.themoviedb.org/3/movie/popular?api_key=0d4cbfe47f14bc16cfefd10fa3dfc816&language=ar');
    setMovies(responsive.data.results)
  };
  //get pages
  const getPages = async(page) => {
    const responsive =await axios.get(`https://api.themoviedb.org/3/movie/popular?api_key=0d4cbfe47f14bc16cfefd10fa3dfc816&language=ar&page=${page}`);
    setMovies(responsive.data.results);
    console.log(responsive.data.total_pages);
  };
  useEffect(()=> {
    AllMovies();
  },[])
    const search = async(word) => {
      const res = await axios.get(`https://api.themoviedb.org/3/search/movie?api_key=0d4cbfe47f14bc16cfefd10fa3dfc816&query=${word}&language=ar`);
      if(word === '') {
        AllMovies()
      }else{
        setMovies(res.data.results)
      }
    }
  return (
    <div className="App">
      <NavBar search = {search} />
      <Container>
        <MovieList movies = {movies} getPages={getPages}/>
      </Container>
    </div>
  );
}

export default App;
