import { Container } from "react-bootstrap";
import NavBar from "./Component/NavBar";
import MovieList from "./Component/MovieList";
import axios from "axios";
import { useState } from "react";
import { Route, Routes } from "react-router-dom";
import MovieDetails from "./Component/MovieDetails";


function App() {
  const [pagecount , setPagecount] = useState(0)
  //get pages
  const getPages = async(page) => {
    const responsive =await axios.get(`https://api.themoviedb.org/3/movie/popular?api_key=0d4cbfe47f14bc16cfefd10fa3dfc816&language=ar&page=${page}`);
    setPagecount(responsive.data.total_pages);
    console.log(responsive.data.total_pages);
  };

  return (
    <div className="App">
      <NavBar  />
      <Routes>
        <Route path="/" element= {<Container>
        <MovieList getPages={getPages} pagecount= {pagecount}/>
      </Container>}/>
        <Route path="/movie/:id" element= {<Container><MovieDetails /></Container>}/>
      </Routes>
    </div>
  );
}

export default App;
