import React from 'react'
import { Container } from 'react-bootstrap';
import ReactPaginate from 'react-paginate';
import './Css/pagination.css';
import { useState } from 'react';
import axios from 'axios';

function Pagenation() {
    const [pagecount , setPagecount] = useState(0)
    //get pages
    const getPages = async(page) => { 
      const responsive =await axios.get(`https://api.themoviedb.org/3/movie/popular?api_key=0d4cbfe47f14bc16cfefd10fa3dfc816&language=ar&page=${page}`);
      setPagecount(responsive.data.total_pages);
      console.log(responsive.data.total_pages);
    };
    const handlePageClick = (data) => {
        getPages(data.selected + 1)
    }
    const pageCount = {pagecount};
    return (
        <Container>
            <ReactPaginate 
            breakLabel="..."
            nextLabel="next >"
            onPageChange={handlePageClick}
            marginPagesDisplayed={1}
            pageRangeDisplayed={2}
            
            pageCount={pageCount}
            previousLabel="< previous"
            renderOnZeroPageCount={null}
            containerClassName={'pagination justify-content-center p-3'}
            pageClassName={'page-item'}
            pageLinkClassName={'page-link'}
            previousClassName={'page-item mx-2'}
            nextLinkClassName={'page-link'}
            previousLinkClassName={'page-link'}
            nextClassName={'page-item mx-2'}
            breakClassName={'page-item'}
            breakLinkClassName={'page-link'}
            activeClassName={'active'}
            />
        </Container>
    )
}

export default Pagenation ;
